require_relative '../swift_template_generator' 

module Redbreast
  module TemplateGenerator
    module Color
      # Used for creating colors in swift.
      class Swift < TemplateGenerator::Swift
        include ERB::Util
        def template
          <<~TEMPLATE
            import UIKit

            //THIS FILE IS AUTOGENERATED, DO NOT EDIT BY HAND
            <%= generate_extension('UIColor', app_name) %>
            <%= generate_file_swift(names: asset_names, type: ': UIColor { return UIColor(named: "', bundle: bundle) %>
            }
          TEMPLATE
        end
      end
    end
  end
end
