require_relative '../swift_template_generator'

module Redbreast
    module TemplateGenerator
        module Color
            class Swift < TemplateGenerator::Swift
                include ERB::Util
                def template()
<<-TEMPLATE
import UIKit

//THIS FILE IS AUTOGENERATED, DO NOT EDIT BY HAND
extension UIColor {
    struct <%= application_name(app_name) %> {}
}

extension UIColor.<%= application_name(app_name) %> {

<%= generate_file(color_names, "\t", "", 'static var ', ': UIColor { return UIColor(named: "', '")! }') %>    
}
TEMPLATE
                end
            end
        end
    end
end