require_relative '../objc_template_generator'

module Redbreast
    module TemplateGenerator
        module Image
            class ObjC < Redbreast::TemplateGenerator::ObjC
                include ERB::Util

                def h_template()
<<-TEMPLATE

#import <UIKit/UIKit.h>

//THIS FILE IS AUTOGENERATED, DO NOT EDIT BY HAND
<%= generate_category('interface', 'UIImage', app_name) %>
<%= generate_h_file_objc(image_names, '+ (UIImage *)', ';')%>
@end

TEMPLATE
                end
                    
                def m_template()
<<-TEMPLATE

#import <UIKit/UIKit.h>

//THIS FILE IS AUTOGENERATED, DO NOT EDIT BY HAND
<%= generate_category('implementation', 'UIImage', app_name) %>
<%= generate_m_file_objc(image_names, '+ (UIImage *)', '\n{\n\t[UIImage imageNamed:\@\"', '\" inBundle:', bundle, ' compatibleWithTraitCollection:nil];\n}\n') %>
@end

TEMPLATE
                end

            end
        end
    end
  end